mapper.insert:

SELECT NVL(MAX(mail_id),0)+1 FROM mail 

 INSERT INTO mail 
 (mail_id, mail_sender, mail_receiver, mail_content, 
 mail_status, used_id, mail_date, used_name, sendnick, rcvnick, mail_del)
 VALUES
 (#{mailId}
  ,#{mailSender}
  ,#{mailReceiver}
  ,#{mailContent}
  ,0
  ,#{usedId}
  ,sysdate
  ,#{usedName}
  ,#{sendNick}
  ,#{rcvNick}
  ,0)

mapper.update:
UPDATE mail 
SET mail_status = 1 
WHERE mail_id = #{mailId}

UPDATE mail 
SET mail_del = 1 
WHERE mail_id = #{mailId}
