		,starting_point	
		,camping_point	

		,note_id	

		,recru_num	
		,closing_date
		,nickname	
		)
	VALUES(
		#{recruId}
		,#{writer}
		,#{recruTitle}

		,#{recruContent}


		,#{wishAge}

		,#{wishSex}
		,#{carYn}

		,#{myGear}


		,#{needGear}

		,#{goDate}
		,#{comeDate}
		,#{startingPoint}
		,#{campingPoint}

		,#{noteId}

		,#{recruNum}
		,#{closingDate}
		,#{nickname}
)

mapper.update:
UPDATE recruitment 
SET status = #{status}
WHERE recru_id = #{recruId}
