mapper.update:
UPDATE recruitment SET recru_title = #{recruTitle}
                    ,recru_content = #{recruContent}
                    ,wish_age = #{wishAge}
                    ,wish_sex = #{wishSex}
                    ,car_yn = #{carYn}
                    ,my_gear=#{myGear}
                    ,need_gear = #{needGear}
                    ,go_date = #{goDate}
                    ,come_date = #{comeDate}
                    ,starting_point = #{startingPoint}
                    ,camping_point = #{campingPoint}
                    ,note_id = #{noteId}
                    ,recru_num= #{recruNum}
                    ,closing_date = #{closingDate}
WHERE recru_id = #{recruId}

UPDATE recruitment 
   SET status = #{status}
 WHERE recru_id = #{recruId}


