mapper.update:
UPDATE used SET used_name = #{usedName}, used_category = #{usedCategory}, used_price = #{usedPrice},
used_place = #{usedPlace},
used_content = #{usedContent},
used_condition = #{usedCondition}
WHERE used_id = #{usedId}

UPDATE used 
SET used_status = 1
WHERE used_id = #{usedId}

UPDATE used 
SET used_status = 2
WHERE used_id = #{usedId}

UPDATE used
SET deal_status = #{dealStatus}
WHERE used_id = #{usedId}

UPDATE used
SET used_cnt = used_cnt+1
WHERE used_id = #{usedId}

UPDATE used
SET used_like = used_like+1
WHERE used_id = #{usedId}

mapper.insert:

SELECT NVL(MAX(used_id),0)+1 FROM used 

INSERT INTO used
				(used_id,
				used_name, 
				used_category, 
				used_price, 
				used_place, 
				used_content,
				used_condition,
				email,
				nickname
				)
		VALUES 
			 (#{usedId}
			 ,#{usedName}
			 ,#{usedCategory}
			 ,#{usedPrice}
			 ,#{usedPlace}
			 ,#{usedContent}
			 ,#{usedCondition}
			 ,#{email}
			 ,#{nickName}
			)
